package com.saku.warga

import android.content.Intent
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import com.google.android.material.snackbar.Snackbar
import com.saku.warga.adapter.DonasiAdapter
import com.saku.warga.apihelper.UtilsApi
import com.saku.warga.models.DonasiData
import kotlinx.android.synthetic.main.activity_donasi.*
import okhttp3.ResponseBody
import org.json.JSONObject
import retrofit2.Call
import retrofit2.Callback
import retrofit2.Response

class DonasiActivity : AppCompatActivity() {
    var preferences  = Preferences()
    private val dataArray= mutableListOf<DonasiData>()
    private lateinit var dataAdapter:DonasiAdapter

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_donasi)
        preferences.setPreferences(this)
        back.setOnClickListener {
            onBackPressed()
        }
        Snackbar.make(findViewById(android.R.id.content),"Menu ini masih dalam tahap pengembangan.",
            Snackbar.LENGTH_LONG).show()
        dataAdapter = DonasiAdapter(this)
        recyclerview.layoutManager = LinearLayoutManager(this)
        recyclerview.adapter = dataAdapter
        dataArray.add(
            DonasiData("01","Pembangunan Masjid","data:image/jpeg;base64,"
            ,"1000000000","780000000","10 Hari","wajib")
        )
        dataArray.add(
            DonasiData("02","Pembangunan Masjid","data:image/jpeg;base64,"
            ,"1000000000","780000000","10 Hari","tidak wajib")
        )
        dataAdapter.initData(dataArray)
//        initData()
    }

    fun initData() {
        val apiservice = UtilsApi().getAPIService(this@DonasiActivity)
        apiservice?.iuranDetail()?.enqueue(object : Callback<ResponseBody?> {
            override fun onResponse(
                call: Call<ResponseBody?>,
                response: Response<ResponseBody?>
            ) {
                if (response.isSuccessful) {
                    if (response.body() != null) {
                        try {

                            val obj = JSONObject(response.body()!!.string())
//                            val data = JSONArray(obj.optString("user"))
                        } catch (e: Exception) {

                        }
                    }else{
                        Toast.makeText(this@DonasiActivity, "Terjadi kesalahan", Toast.LENGTH_SHORT).show()
                    }
                } else if(response.code() == 422) {
                    Toast.makeText(this@DonasiActivity, "Terjadi kesalahan", Toast.LENGTH_SHORT).show()
                } else if(response.code() == 401){
                    val intent = Intent(this@DonasiActivity, LoginActivity::class.java)
                    startActivity(intent)
                    preferences.preferencesLogout()
                    finish()
                    Toast.makeText(this@DonasiActivity, "Sesi telah berakhir, silahkan login kembali", Toast.LENGTH_SHORT).show()
                } else if(response.code() == 403){
                    Toast.makeText(this@DonasiActivity, "Unauthorized", Toast.LENGTH_SHORT).show()
                } else if(response.code() == 404){
                    Toast.makeText(this@DonasiActivity, "Terjadi kesalahan server", Toast.LENGTH_SHORT).show()
                }
            }

            override fun onFailure(call: Call<ResponseBody?>, t: Throwable) {
                Toast.makeText(this@DonasiActivity, "Koneksi Bermasalah", Toast.LENGTH_SHORT).show()
            }
        })
    }

}
